<?phprequire_once(dirname(dirname(dirname( dirname( __FILE__ ) ))) . '/wp-config.php' );global $wpdb;$url = plugins_url('', __FILE__) . "/"; ?><script>var uri = "<?php echo plugins_url('', __FILE__);?>" </script><script type="text/javascript">jQuery("select, input:checkbox, input:radio").uniform();</script><?phpif(isset($_REQUEST['emp_id']))	{		$empid=intval($_REQUEST['emp_id']);		// select all allocated services to employees from allocation table to show their services			$ser = $wpdb->get_results    	(       		 $wpdb->prepare       		 (        	    'SELECT '.$wpdb->prefix.'sm_services.id,'.$wpdb->prefix.'sm_services.name,"1" as Checked FROM '.$wpdb->prefix.'sm_services join '.$wpdb->prefix.'sm_allocate_serv on  ' . $wpdb->prefix .'sm_services.id  = ' . $wpdb->prefix . 'sm_allocate_serv.serv_id  where ' . $wpdb->prefix .'sm_allocate_serv.emp_id = %d union all SELECT ' . $wpdb->prefix .'sm_services.id,' . $wpdb->prefix . 'sm_services.name,"0" as Checked FROM ' . $wpdb->prefix . 'sm_services where ' . $wpdb->prefix .'sm_services.id not in (select ' . $wpdb->prefix . 'sm_allocate_serv.serv_id from ' . $wpdb->prefix . 'sm_allocate_serv where ' . $wpdb->prefix .'sm_allocate_serv.emp_id = %d)',         	   $empid,         	   $empid         	  )   		 );		for($b=0;$b < count($ser); $b++)		{			$name = $ser[$b]->name;			$idd = $ser[$b]->id;			$check =  $ser[$b]->Checked;			if($check=="1")			{				?>				<input id="<?php echo $idd; ?>" checked="checked" name="allocateservice" value="<?php echo $name; ?>" type="checkbox"  />				<label for="allservice"><?php echo $name; ?></label>				<br />				<?php 			}			else			{				?>				<input id="<?php echo $idd; ?>"  name="allocateservice" value="<?php echo $name; ?>" type="checkbox"  />				<label for="allservice"><?php echo $name; ?></label>				<br />				<?php			}		}  	}?>