<?phprequire_once(dirname(dirname(dirname( dirname( __FILE__ ) ))) . '/wp-load.php' );require_once(dirname(dirname(dirname( dirname( __FILE__ ) ))) . '/wp-config.php' );global $wpdb;$fieldcompare = html_entity_decode($_REQUEST['fieldcompare']);$fieldtoupdate = html_entity_decode($_REQUEST['fieldtoupdate']);$radiovisible = intval($_REQUEST['radiovisible']);$radioo2require = intval($_REQUEST['radioo2req']);$wpdb->query   (         $wpdb->prepare         (               "UPDATE ".$wpdb->prefix."sm_booking_field SET status = %d  WHERE field_name = %s",               $radiovisible,               $fieldcompare         )   );if ($radiovisible == "0") {	$wpdb->query   (         $wpdb->prepare         (               "UPDATE ".$wpdb->prefix."sm_booking_field SET required = %d  WHERE field_name = %s",               0,               $fieldcompare         )   );	} else {	if ($radioo2require == "1") 	{		$wpdb->query   		(        	 $wpdb->prepare        	 (          	     "UPDATE ".$wpdb->prefix."sm_booking_field SET required = %d  WHERE field_name = %s",          	     1,          	     $fieldcompare        	 )  		 );				} 	else 	{		$wpdb->query   		(        	 $wpdb->prepare        	 (          	     "UPDATE ".$wpdb->prefix."sm_booking_field SET required = %d  WHERE field_name = %s",          	     0,          	     $fieldcompare        	 )  		 );			}}$wpdb->query(     $wpdb->prepare      (          	"UPDATE ".$wpdb->prefix."sm_booking_field SET field_name2 = %s  WHERE field_name = %s",          	$fieldtoupdate,          	$fieldcompare      ) );?>